mapper.sql:
from
	codeGroup a
where 1 = 1
	and delNy = #{shDelNy}
	and cgUseNy = #{shcgUseNy}

		AND a.cgDateTime BETWEEN #{shcgDateStart} AND #{shcgDateEnd}
		AND a.cgDateTimeSvr BETWEEN #{shcgDateStart} AND #{shcgDateEnd}


		and seq = #{shcgValue}
		and cgName like concat('%', #{shcgValue}, '%')

mapper.update:
update codeGroup
	set
		cgName = #{cgName}
	    ,cgUseNy = #{cgUseNy}
	    ,cgOrder = #{cgOrder}
	    ,cgDesc = #{cgDesc}
	    ,delNy = 0
	    ,cgDateTime = now()
	    ,cgDateTimeSvr = now()
	where 1 = 1
		and seq = #{seq}

mapper.delete:
DELETE FROM codeGroup
	WHERE 1 = 1
		AND seq = #{seq}
