mapper.sql:
from
	codeGroup a
where 1 = 1
	and delNy = #{shDelNy}
	and cgUseNy = #{shUseNy}

		AND seq = #{shValue}
		AND cgName LIKE CONCAT('%', #{shValue}, '%')


		AND a.cgDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
		AND a.cgDateTimeSvr BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.update:
update codeGroup
	set
		cgName = #{cgName}
	    ,cgUseNy = #{cgUseNy}
	    ,cgOrder = #{cgOrder}
	    ,cgDesc = #{cgDesc}
	    ,delNy = 0
	    ,cgDateTime = now()
	    ,cgDateTimeSvr = now()
	where 1 = 1
		and seq = #{seq}

mapper.delete:
DELETE FROM codeGroup
	WHERE 1 = 1
		AND seq = #{seq}
