mapper.sql:
from
	code a
	join codegroup as b
		on b.seq = a.codeGroup_seq
WHERE 1 = 1
	AND a.delNy = #{shDelNy}
	AND cUseNy = #{shUseNy}

		AND a.seq = #{shValue}
		AND cName LIKE CONCAT('%',#{shValue},'%')


		AND a.cDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
		AND a.cDateTimeSvr BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.update:
update code
	set
		cName = #{cName}
	    ,cUseNy = #{cUseNy}
	    ,cOrder = #{cOrder}
	    ,cDesc = #{cDesc}
	    ,delNy = 0
	    ,cDateTime = now()
	    ,cDateTimeSvr = now()
	    ,codeGroup_seq = #{codeGroup_seq}
	where 1 = 1
		and seq = #{seq}

mapper.delete:
delete from code
	where 1 = 1
		and seq = #{seq}
