mapper.select:
SELECT aa.* FROM ( SELECT a.hallSeq ,a.hallName ,a.hallAdr ,a.hallAdrDt ,a.hallZipNum ,a.hallDesc ,a.hallUseNy ,a.hallDelNy ,a.hallWi ,a.hallGy ,a.hallDateTimeReg ,a.hallDateTimeEdit FROM concertHall AS a WHERE 1 = 1 AND hallDelNy = #{shDelNy} AND hallUseNy = #{shUseNy}
AND hallSeq = #{shOption} AND hallName LIKE CONCAT('%', #{shValue}, '%')
AND a.hallDateTimeReg BETWEEN #{shDateStart} AND #{shDateEnd} AND a.hallDateTimeEdit BETWEEN #{shDateStart} AND #{shDateEnd}
ORDER BY a.hallSeq DESC ) aa limit #{rowNumToShow} offset #{startRnumForMysql}

mapper.insert:
INSERT INTO concertHall (
	hallName
	,hallAdr
	,hallAdrDt
	,hallZipNum
	,hallDesc
	,hallUseNy
	,hallDelNy
	,hallWi
	,hallGy
	,hallDateTimeReg
	,hallDateTimeEdit
)
VALUES (
	#{hallName}
	,#{hallAdr}
	,#{hallAdrDt}
	,#{hallZipNum}
	,#{hallDesc}
	,1
	,0
	,#{hallWi}
	,#{hallGy}
	,now()
	,now()
)

	SELECT last_insert_id()

mapper.update:
UPDATE concertHall
	SET
		hallName = #{hallName}
		,hallName = #{hallName}
		,hallAdr = #{hallAdr}
		,hallAdrDt = #{hallAdrDt}
		,hallZipNum = #{hallZipNum}
		,hallDesc = #{hallDesc}
		,hallUseNy = #{hallUseNy}
		,hallWi = #{hallWi}
		,hallGy = #{hallGy}
		,hallDateTimeReg = now()
		,hallDateTimeEdit = now()
	WHERE 1 = 1
		AND hallSeq = #{hallSeq}

mapper.delete:
DELETE FROM concertHall
	WHERE 1 = 1
		AND hallSeq = #{hallSeq}
