mapper.select:
SELECT aa.* FROM ( SELECT a.concertSeq ,a.concertName ,a.concertPlayTime ,a.concertDate ,a.concertSinger ,a.concertAge ,a.concertGenre ,a.concertDesc ,a.concertDateTimeReg ,a.concertDateTimeEdit ,a.concertUseNy ,a.concertDelNy FROM concert AS a WHERE 1 = 1 AND concertDelNy = #{shDelNy} AND concertUseNy = #{shUseNy}
AND concertSeq = #{shOption} AND concertName LIKE CONCAT('%', #{shValue}, '%')
AND a.concertDateTimeReg BETWEEN #{shDateStart} AND #{shDateEnd} AND a.concertDateTimeEdit BETWEEN #{shDateStart} AND #{shDateEnd}
ORDER BY a.concertSeq DESC ) aa limit #{rowNumToShow} offset #{startRnumForMysql}

mapper.insert:
INSERT INTO concert (
	concertName
	,concertPlayTime
	,concertDate
	,concertSinger
	,concertAge
	,concertGenre
	,concertDesc
	,concertDateTimeReg
	,concertDateTimeEdit
	,concertUseNy
	,concertDelNy
)
VALUES (
	#{concertName}
	,#{concertPlayTime}
	,#{concertDate}
	,#{concertSinger}
	,#{concertAge}
	,#{concertGenre}
	,#{concertDesc}
	,now()
	,now()
	,1
	,0
)

	SELECT last_insert_id()

mapper.update:
UPDATE concert
	SET
		concertName = #{concertName}
		,concertPlayTime = #{concertPlayTime}
		,concertDate = #{concertDate}
		,concertSinger = #{concertSinger}
		,concertAge = #{concertAge}
		,concertGenre = #{concertGenre}
		,concertDesc = #{concertDesc}
		,concertDateTimeReg = now()
		,concertDateTimeEdit = now()
		,concertUseNy = #{concertUseNy}
	WHERE 1 = 1
		AND concertSeq = #{concertSeq}

mapper.delete:
DELETE FROM concert
	WHERE 1 = 1
		AND concertSeq = #{concertSeq}
