mapper.sql:
FROM
	member AS a
WHERE 1 = 1
	AND mmDelNy = #{shDelNy}
	AND mmUseNy = #{shUseNy}

		AND mmSeq = #{shValue}
		AND mmName LIKE CONCAT('%', #{shValue}, '%')


		AND mmDateTimeReg BETWEEN #{shDateStart} AND #{shDateEnd}
		AND mmDateTimeEdit BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO member(
	mmName
	,mmGender
	,mmId
	,mmEmail
	,mmPw
	,mmBirth
	,mmPhone
	,mmDateTimeReg
	,mmDateTimeEdit
	,mmUseNy
	,mmDelNy
	,mmZipNum
	,mmAdr
	,mmAdrDt
)
VALUE(
	#{mmName}
    ,#{mmGender}
    ,#{mmId}
    ,#{mmEmail}
    ,#{mmPw}
    ,#{mmBirth}
    ,#{mmPhone}
    ,now()
    ,now()
    ,1
    ,0
    ,#{mmZipNum}
	,#{mmAdr}
	,#{mmAdrDt}
)

	SELECT last_insert_id()

INSERT INTO member(
	mmName
	,mmGender
	,mmId
	,mmEmail
	,mmPw
	,mmBirth
	,mmPhone
	,mmDateTimeReg
	,mmDateTimeEdit
	,mmUseNy
	,mmDelNy
	,mmZipNum
	,mmAdr
	,mmAdrDt
)
VALUE(
	#{mmName}
    ,#{mmGender}
    ,#{mmId}
    ,#{mmEmail}
    ,#{mmPw}
    ,#{mmBirth}
    ,#{mmPhone}
    ,now()
    ,now()
    ,1
    ,0
    ,#{mmZipNum}
	,#{mmAdr}
	,#{mmAdrDt}
)

	SELECT last_insert_id()

mapper.update:
UPDATE member
	SET
		mmName = #{mmName}
		,mmGender = #{mmGender}
		,mmId = #{mmId}
		,mmEmail = #{mmEmail}
		,mmPw = #{mmPw}
		,mmBirth = #{mmBirth}
		,mmPhone = #{mmPhone}
		,mmDateTimeReg = now()
		,mmDateTimeEdit = now()
		,mmUseNy = 1
		,mmDelNy = 0
		,mmZipNum = #{mmZipNum}
		,mmAdr = #{mmAdr}
		,mmAdrDt = #{mmAdrDt}
	WHERE 1 = 1
		AND mmSeq = #{mmSeq}

mapper.delete:
DELETE FROM member
	WHERE 1 = 1
	AND mmSeq = #{mmSeq}
